import React, { useState, useEffect } from 'react';
import axios from '../../utils/axios';
import Nav from '../../components/browse/Nav';
import SearchResult from '../../components/search/SearchResult';
import requests from '../../utils/requests';
import './Search.css';

const Search = () => {
	const [query, setQuery] = useState({});
	const [searchInput, setSearchInput] = useState('');
	const [searchGenre, setSearchGenre] = useState('');
	const [searchType, setSearchType] = useState('');
	const [searchLanguage, setSearchLanguage] = useState('');
	const [searchYear, setSearchYear] = useState('');
	const [genres, setGenres] = useState([]);
	const [mediaTypes, setMediaTypes] = useState([]);
	const [mess, setMess] = useState('');

	useEffect(() => {
		async function fetchGenres() {
			const request = await axios.get(`${requests.fetchGenres}`);
			setGenres(request.data);
			return request;
		}
		async function fetchMediaTypes() {
			const request = await axios.get(`${requests.fetchMediaTypes}`);
			setMediaTypes(request.data);
			return request;
		}

		fetchGenres();
		fetchMediaTypes();
	}, []);
	console.log(searchInput);
	const handleSearch = () => {
		setQuery({keyword: searchInput, genre: searchGenre, mediaType: searchType, language: searchLanguage, year: searchYear});
		if(searchInput==='') {
			setMess("NO TYPE KEYWORDS")
		} else {
			setMess("NO MOVIES FOUND")
		}
	}

	const resetSearch = () => {
		setQuery('');
		setSearchInput('');
		setSearchGenre('');
		setSearchType('');
		setSearchLanguage('');
		setSearchYear('');
	}

	return (
		<div className='app'>
			<Nav />
			<div className='s009'>
				<form>
					<div className='inner-form'>
						<div className='basic-search'>
							<div className='input-field'>
								<input
									type='text'
									placeholder='Type Keywords'
									onChange={(e) => setSearchInput(e.target.value)}
									value={searchInput}
								/>
								<div className='icon-wrap'>
									<svg
										className='svg-inline--fa fa-search fa-w-16'
										fill='#ccc'
										aria-hidden='true'
										data-prefix='fas'
										data-icon='search'
										role='img'
										xmlns='http://www.w3.org/2000/svg'
										viewBox='0 0 512 512'>
										<path d='M505 442.7L405.3 343c-4.5-4.5-10.6-7-17-7H372c27.6-35.3 44-79.7 44-128C416 93.1 322.9 0 208 0S0 93.1 0 208s93.1 208 208 208c48.3 0 92.7-16.4 128-44v16.3c0 6.4 2.5 12.5 7 17l99.7 99.7c9.4 9.4 24.6 9.4 33.9 0l28.3-28.3c9.4-9.4 9.4-24.6.1-34zM208 336c-70.7 0-128-57.2-128-128 0-70.7 57.2-128 128-128 70.7 0 128 57.2 128 128 0 70.7-57.2 128-128 128z'></path>
									</svg>
								</div>
							</div>
						</div>
						<div className='advance-search'>
							<div className='row third'>
								<div className='input-select'>
									<select name="genres" id="genres" value={searchGenre} onChange={(e) => setSearchGenre(e.target.value)}>
										<option hidden>Select Genre</option>
										{genres.map(genre => {
											return (
												<option value={genre.name} key={genre.id}>{genre.name}</option>
											);
										})}
									</select>
								</div>
								<div className='input-select'>
									<select name="mediaTypes" id="mediaTypes" value={searchType}  onChange={(e) => setSearchType(e.target.value)}>
										<option hidden>Select Type</option>
										{mediaTypes.map(type => {
											return (
												<option value={type} key={mediaTypes.indexOf(type)}>{type}</option>
											);
										})}
									</select>
								</div>
								<div className='input-select'>
									<select name="languages" id="languages" value={searchLanguage} onChange={(e) => setSearchLanguage(e.target.value)}>
										<option hidden>Select Language</option>
										<option value="en">English</option>
										<option value="ja">Japanese</option>
										<option value="ko">Korean</option>
									</select>
								</div>

								<div className='input-select'>
									<input type="number" name="years" min="1900" max="2099" step="1" placeholder="Year" value={searchYear} onChange={(e) => setSearchYear(e.target.value)} />
								</div>
							</div>
							<div className='button-search'>
								<div className='row third'>
									<div className='input-field'>
										<div className='result-count'>

										</div>
										<div className='group-btn'>
											<button
												className='btn-delete'
												onClick={resetSearch}
												type='button'
											>
												RESET
											</button>
											<button
												className='btn-search'
												type='button'
												onClick={() => handleSearch()}
											>SEARCH</button>
										</div>
									</div>
								</div>
							</div>
						</div>
					</div>
				</form>
			</div>
			<SearchResult query={query} mess={mess}/>
		</div>
	);
};

export default Search;
